import React from 'react';
import './App.css';
import PropTypes from 'prop-types';

class Pokemon extends React.Component {
    //   static propTypes = {
    //     rentals: PropTypes.array.isRequired,
    //     onBookRental: PropTypes.func.isRequired
    //   }

    constructor(props) {
        super(props);
        this.state = {
            name: {},
            pokeIdx: '',
            url: ''
        }
    }

    componentDidMount() {
        const { name, pokeIdx, url } = this.props;
console.log(this.props)
        // this.setState({
        //     name: this.props.name,
        //     idx: this.props.idx,
        //     url: this.props.url
        // });

        this.setState(prevState => {
            return {
                name: name,
                pokeIdx: pokeIdx,
                url: url 
            }});
    }
    render () {
        return (
            <div className="">
                <h3>
                    {this.state.name.toUpperCase()}, #{this.state.idx}
                </h3>
                <img src="http://pokeapi.co/media/sprites/pokemon/12.png" alt={this.state.name.toUpperCase()} />
            </div>
        );
    }
}

export default Pokemon;
