    //     onBookRental: PropTypes.func.isRequired
    //   }

    constructor(props) {
        super(props);
        this.state = {
            name: {},
            pokeIdx: '',
            url: ''
        }
    }

    componentDidMount() {
        //const { name, pokeIdx, url } = this.props;
    console.log(this.props.name)
        // this.setState({
        //     name: this.props.name,
        //     idx: this.props.idx,
        //     url: this.props.url
        // });

        this.setState(prevState => {
            return {
                name: this.props.name,
                pokeIdx: this.props.pokeIdx,
                url: this.props.url 
            }});
            // console.log(this.state.name)
            // console.log(this.state.pokeIdx)

            // console.log(this.state.url)

    }
    render () {
        const imgUrl = `http://pokeapi.co/media/sprites/pokemon/${this.state.pokeIdx}.png`;
        return (
            <div className="">
                <h3>
                    {this.state.name}
                </h3>
                <h3>
                    {this.state.pokeIdx}
                </h3>
                <img src={imgUrl} alt={this.state.name} />
            </div>
        );
    }
}

export default Pokemon;
