import React from 'react';
import logo from './logo.svg';
import './App.css';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: {},
      isLoading: true,
      hasError: false
    }
  }

  componentDidMount() {
    // TODO edit URL
    fetch('https://pokeapi.co/api/v2/pokemon')
      .then(response => {
        console.log(response);
        console.log(response.json());
        return response.json();
      })
      .then(data => {
        this.setState({
          data: data.message,
          isLoading: false
        });
      })
      .catch(error => {
        this.setState({
          hasError: true,
          isLoading: false
        });
      });
  }


  // const Pokedex = require('pokeapi-js-wrapper');
  // const P = new Pokedex.Pokedex();

  // P.getPokemonByName("pokedex-promise-v2")
  //   .then(function(response) {
  //     console.log(response);
  //     //TODO store pokemon in pokemonGroup
  //   });

  render (){
    if (this.state.isLoading) {
      return <div>Loading...</div>;
    }

    if (this.state.hasError) {
      return <div>ERROR, please reload and try again</div>;
    }

    // TODO map pokemon
    const pokemonGroup = Object.keys(this.state.data)
    .map((pokemon, idx) => <li key={idx}>{pokemon}</li>);


    return(
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
        </header>
        <div>
          {pokemonGroup}
        </div>
      </div>
    )
  }
}

export default App;
