import React from 'react';
import './Pokemon.css';
import Pokemon from './Pokemon.js';
// import PokemonCard from './PokemonCard.js';
// import {
//   BrowserRouter as Router,
//   Route,
//   Switch,
//   Link
// } from 'react-router-dom';


class PokemonPage extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        data: {},
        isLoading: true,
        hasError: false
      }
    }

    componentDidMount() {
        fetch('https://pokeapi.co/api/v2/pokemon/?limit=20')
        .then(response => {
            // console.log(response);
            // console.log(response.json());
            // console.log(response.json());
            // console.log(response.JSON.stringify()); // data can be `string` or {object}!
            return response;
        }).then(res => {
            // console.log(res);
            return res.json();
        })
        .then(data => {
            // console.log(data);
            // console.log(data.results[0].name);
            this.setState({
                data: data.results,
                isLoading: false
            });
            // console.log(this.state.data);
            // console.log('Success:', JSON.stringify(data))
      });



    };
    render (){
        if (this.state.isLoading) {
            return <div>Loading...</div>;
        };

        if (this.state.hasError) {
            return <div>ERROR, please reload and try again</div>;
        };

        // this.state.data.map((pokemonID, idx) =>
        // console.log(data);
        // data.map((pokemon,id) => {

        //         console.log(pokemon.name);
        //      return(null);
        // });

        // const url = "https://pokeapi.co/api/v2/pokemon/";
        //                        <Link to={`/pokemon/${pokemon.name}`}>{pokemon.name}</Link>

        //<img src={pokemon.sprites.front_default} alt={pokemon.name} />

        const data =  this.state.data;
        console.log(data);
        console.log(data[0].name)
        // TODO create PokemonCard inputs
        const pokemonGroup = () => {
            data.prototype.map((pokemon, id) => {
                return(
                    <li idx={id} key={id}>
                        <Pokemon name={pokemon.name} pokeIdx={id+1} url={pokemon.url} />
                    </li>
                );
            });
        }

        return (
            <div>
                <h1>Pokemon Group</h1>

                <ul>
                    {pokemonGroup}

                </ul>
                <hr />                   
            </div> 
        );
    }
}
export default PokemonPage;
