import React from 'react';
import logo from './logo.svg';
import './Pokemon.css';
import Pokemon from 'PokemonCard.js';
import {
  BrowserRouter as Router,
  Route,
  Switch,
  Link
} from 'react-router-dom';


class PokemonPage extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        data: {},
        isLoading: true,
        hasError: false
      }
      // const pokemonLinks = pokemonGroup.map((pokemon, idx) => {
      //   return <Link key={idx} to={`/pokemon/${pokemon}`}>{pokemon}</Link>
      // });
    }

    componentDidMount() {
        // TODO edit URL
        fetch('https://pokeapi.co/api/v2/pokemon/' + match.params.id)
          .then(response => {
            console.log(response);
            console.log(response.json());
            return response;
          })
          .then(data => {
            console.log(data);
            console.log(data[0]);
            this.setState({
              data: data.results,
              isLoading: false
            });
            console.log(this.state.data);
          })
          .catch(error => {
            this.setState({
              hasError: true,
              isLoading: false
            });
            console.log(this.state.data);

          });
      }
      render (){
        if (this.state.isLoading) {
          return <div>Loading...</div>;
        }

        if (this.state.hasError) {
          return <div>ERROR, please reload and try again</div>;
        }

            //   <>
            //   <ul>
            //     {pokemonGroup.map(({ name, id }) => (
            //       <li key={id}>
            //         <Link to={`${match.url}/${name}`}>{name}</Link>
            //       </li>
            //     ))}
            //   </ul>
            //   <Route path={`${match.url}/:name`} component={PokemonGroup} />
            //   <hr />
            // </>
        const pokemonGroup = this.state.data.map((pokemonID, idx) => {
            return (
                <Pokemon pokemonID={pokemonID} key={idx} idx={idx} />
            );
        })
        return (
            <div>
                {pokemonGroup}
            </div>
        );

    }
 }

 export default PokemonPage;
