import React from 'react';
import './Pokemon.css';
import PokemonCard from './PokemonCard.js';
import {
  BrowserRouter as Router,
  Route,
  Switch,
  Link
} from 'react-router-dom';


class PokemonPage extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        data: {},
        isLoading: true,
        hasError: false
      }
    }

    componentDidMount() {
        fetch('https://pokeapi.co/api/v2/pokemon/')
        .then(response => {
            // console.log(response);
            // console.log(response.json());
            // console.log(response.json());
            // console.log(response.JSON.stringify()); // data can be `string` or {object}!
            return response;
        }).then(res => {
            console.log(res);
            return res.json();
        })
        .then(data => {
            console.log(data);
            console.log(data.results[0].name);
            this.setState({
                data: data.results,
                isLoading: false
            });
            console.log(this.state.data);
            console.log('Success:', JSON.stringify(data))
        });
    };

    render (){
        if (this.state.isLoading) {
            return <div>Loading...</div>;
        };

        if (this.state.hasError) {
            return <div>ERROR, please reload and try again</div>;
        };

        // this.state.data.map((pokemonID, idx) =>
        const pokemonGroup =  this.state.data;
        return (
            <div>
                {pokemonGroup[4].name}

                <ul>
                    {pokemonGroup.map(({ name, id }) => (
                    <li key={id}>
                        <Link to={`${match.url}/${name}`}>{name}</Link>
                    <Route path={`${match.url}/:name`} component={PokemonCard} />
                    </li>
                    ))}                
                </ul>
                <hr />                   
            </div> 
        );

    }
}
export default PokemonPage;
