    const pokemonLinks = pokemonGroup.map((name, idx) => {
      return <Link key={idx} to={`/pokemon/${name}`}>{name}</Link>
    });

    return(
      <div className="App">
        <Router>
          <Link to="/">Home</Link>
          <Link to="/pokemon">Pokemon</Link>

          <hr />
          {pokemonLinks}

