import React from 'react';
import './App.css';
import PropTypes from 'prop-types';

class Pokemon extends React.Component {
    //   static propTypes = {
    //     rentals: PropTypes.array.isRequired,
    //     onBookRental: PropTypes.func.isRequired
    //   }

    constructor(props) {
        super(props);
        this.state = {
            name: {},
            idx: '',
            url: ''
        }
    }

    componentDidMount() {
        // const { name, idx, url } = this.props;

        // this.setState({
        //     name: this.props.name,
        //     idx: this.props.idx,
        //     url: this.props.url
        // });

        this.setState(prevState => {
            return {
                name: this.props.name,
                idx: this.props.idx,
                url: this.props.url 
            }});
    }
    render () {
        return (
            <div className="">
                <h3>
                    {name.toUpperCase()}, #{idx}
                </h3>
                <img src="http://pokeapi.co/media/sprites/pokemon/12.png" alt={name.toUpperCase()} />
            </div>
        );
    }
}

export default Pokemon;
